document.addEventListener('DOMContentLoaded',function(){var tooltipTriggerList=[].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));var tooltipList=tooltipTriggerList.map(function(tooltipTriggerEl){return new bootstrap.Tooltip(tooltipTriggerEl);});var popoverTriggerList=[].slice.call(document.querySelectorAll('[data-bs-toggle="popover"]'));var popoverList=popoverTriggerList.map(function(popoverTriggerEl){return new bootstrap.Popover(popoverTriggerEl);});document.querySelectorAll('a[href^="#"]').forEach(anchor=>{anchor.addEventListener('click',function(e){e.preventDefault();const target=document.querySelector(this.getAttribute('href'));if(target){target.scrollIntoView({behavior:'smooth',block:'start'});}});});const animateOnScroll=function(){const elements=document.querySelectorAll('.animate-on-scroll');elements.forEach(element=>{const elementTop=element.getBoundingClientRect().top;const elementBottom=element.getBoundingClientRect().bottom;if(elementTop<window.innerHeight&&elementBottom>0){element.classList.add('fade-in');}});};window.addEventListener('scroll',animateOnScroll);animateOnScroll();const forms=document.querySelectorAll('.needs-validation');forms.forEach(form=>{form.addEventListener('submit',function(event){if(!form.checkValidity()){event.preventDefault();event.stopPropagation();}
form.classList.add('was-validated');});});const galleryImages=document.querySelectorAll('.gallery-image');galleryImages.forEach(image=>{image.addEventListener('click',function(){const modal=new bootstrap.Modal(document.getElementById('imageModal'));const modalImage=document.getElementById('modalImage');modalImage.src=this.src;modal.show();});});const newsletterForm=document.querySelector('.newsletter-form');if(newsletterForm){newsletterForm.addEventListener('submit',function(e){const emailInput=this.querySelector('input[type="email"]');if(!emailInput.value){e.preventDefault();alert('Please enter a valid email address');}});}
const navbarToggler=document.querySelector('.navbar-toggler');if(navbarToggler){navbarToggler.addEventListener('click',function(){document.querySelector('.navbar-collapse').classList.toggle('show');});}
const scrollTopBtn=document.getElementById('scrollTopBtn');if(scrollTopBtn){window.addEventListener('scroll',function(){if(window.pageYOffset>100){scrollTopBtn.style.display='block';}else{scrollTopBtn.style.display='none';}});scrollTopBtn.addEventListener('click',function(){window.scrollTo({top:0,behavior:'smooth'});});}});;